import "./App.css";
import Transection from "./components/Transection";
import FormsComponent from "./components/FormsComponent";
import { useState } from "react";


function App() {
  /*
  const initData =[
    {id:1,title:"ค่ารักษาพยาบาล",amount :2000},
    {id:2,title:"ค่าเทอม",amount :16000},
    {id:3,title:"ค่ากิน",amount :500},
  ]
  */
  const [items,setItem] = useState([]) // ภายใน useState คือการอัพเดตค่าที่เป็นรูปแบบ Array เหมือน initData  
  const onAddNewItem =(newItem)=>{ // newItem ไว้รับข้อมูลจาก FormComponent
    setItem((prevItem) =>{
      return [newItem,...prevItem] // รูปแบบคำสั่งที่ให้ค่าใหม่มาอยู่หน้าค่าเก่า
    })
  }
  return (
    <div className="contrainer">
      <h1 style={{color:'red',textAlign:'center'}}>แอพรายรับ - รายจ่าย</h1>
      <FormsComponent onAddItem={onAddNewItem}/>
      <Transection items = {items}/> {/* การ prop ข้อมูลส่งไปให้ Transection*/}
    </div>
  );
}

export default App;
