import "./App.css";
import Transection from "./components/Transection";
import FormsComponent from "./components/FormsComponent";
import { useState } from "react";
import DataContext from "./data/DataContext";
import ReportComponent from "./components/ReportComponent";

function App() {

  const initData =[
    {id:1,title:"ค่าเช่าบ้าน",amount :-2000},
    {id:2,title:"เงินเดือน",amount :12000},
    {id:3,title:"ค่าเดินทาง",amount :-500},
    {id:4,title:"ขายของออนไลน์",amount :-500},
  ]


  const [items,setItem] = useState(initData) // ภายใน useState คือการอัพเดตค่าที่เป็นรูปแบบ Array เหมือน initData  
  const [reportIncome,setReportIncome] = useState(0)
  const [reportExpense,setReportExpense] = useState(0)
  const onAddNewItem =(newItem)=>{ // newItem ไว้รับข้อมูลจาก FormComponent
    setItem((prevItem) =>{
      return [newItem,...prevItem] // รูปแบบคำสั่งที่ให้ค่าใหม่มาอยู่หน้าค่าเก่า
    })
  }
  return (
    <DataContext.Provider value={
      {
        income : 5000,
        expense : -8000
      }
    }>
      <div className="contrainer">
        <h1 style={{color:'red',textAlign:'center'}}>แอพรายรับ - รายจ่าย</h1>
        <ReportComponent/>
        <FormsComponent onAddItem={onAddNewItem}/>
        <Transection items = {items}/> {/* การ prop ข้อมูลส่งไปให้ Transection*/}
      </div>
    </DataContext.Provider>
  );
}

export default App;
